sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    const {data = {}} = event.payload;
    const {record, changes = []} = data;

    if (!record || !changes.length) {
        return;
    }

    if (changes.includes('state')) {
        api.data.fetch_schedule_plans_work_shifts.refresh();

        // refresh calendar if work shift is unlock
        if (record.state.value === '2') { 
            const {results = []} = api.data.skip_refresh_lookup || {};
            const shouldSkipRefresh = results.length > 0 ? results[0].value.value === 'true' : false;

            if (!shouldSkipRefresh) {
                api.emit('SCHEDULE_PLAN_SIDEBAR#REFRESH_CALENDAR');
            }
        }
    }
}
