sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({api, event, helpers, imports}) {
    const {results} = api.data.fetch_agents_in_work_shift || [];
    const agentsInWorkShift = results.map(({agent}) => ({id: agent.value, label: agent.displayValue}));

    api.setState('agentsInWorkShift', agentsInWorkShift);
    api.setState('selectedAgents', [...agentsInWorkShift]);
}
