sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {TransformApiHelpers} params.helpers
 */
function evaluateProperty({
    api,
    helpers
}) {

    const {
        childEvents
    } = api.state.popoverTemplate.event;
    const items = [];

    childEvents.forEach(event => {
        const {
            title,
            startTimeConvertedDisplay,
            endTimeConvertedDisplay,
            schedule
        } = event;
        if (schedule.eventType.sysId === api.state.timeWorkedEvent.sysId || schedule.eventType.sysId === api.state.timeWorkedEvent.inProgressEvent) {
            items.push({
                label: `${startTimeConvertedDisplay}`,
                value: {}
            });
        }
    });

    return items;
}
