sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    const {NowDateTime} = imports['sn_uxf.core_libraries']();
    const {schedulePlanStates} = api.state;
    const {result = {}} = api.data.fetch_schedule_plan_details;
    const {default_start_date = {}, default_end_date = {}, state = {}} = result;

    // move calendar context date to schedule plan start date
    api.emit('SCHEDULE_PLAN_SIDEBAR#CHANGE_CALENDAR_CONTEXT_DATE', {newContextDate: default_start_date.value});

    // set value for canUnpublishSchedule flag
    if (state.value === schedulePlanStates.PUBLISHED) {
        const endDate = NowDateTime(default_end_date.value, 'YYYY-MM-DD');
        const tomorrow = NowDateTime().add(1, 'day');
        api.setState('canUnpublishSchedule', endDate.isAfter(tomorrow, 'day'));
    } else {
        api.setState('canUnpublishSchedule', false);
    }
}
