sys_ux_client_script.script:
/**
  * @param {params} params
  * @param {api} params.api
  * @param {any} params.imports
  */
 function evaluateProperty({api}) {
  const {newAndEditEventModal} = api.state;
  const {modalSchema, formData} = newAndEditEventModal;
  for (const [key, value] of Object.entries(modalSchema)) {
    if (key === 'additionalSelectors') {
      for (const as of value) {
        if (as.mandatory && (!formData[key][as.table] || !formData[key][as.table][as.field]) || as.invalid) {
          return true;
        }
      }
    } else if (value.mandatory) {
      if (Array.isArray(formData[key]) && !formData[key].length) {
        return true;
      }
      if (!formData[key]) {
        return true;
      }
      if (value.invalid) {
        return true;
      }
    }
  }

  return !!newAndEditEventModal.formError || newAndEditEventModal.loading;
}
