sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.imports
 */
 function evaluateProperty({
     api
 }) {
     const {
         category
     } = api.state.popoverTemplate.event;
     const WORK_CATEGORY = '07e433f449a11010f877de942e7c1d18';
     const TIME_OFF_CATEGORY = '066c33f849a11010f877de942e7c1d4b';
     const ON_CALL_CATEGORY = '72dac1553bd02010e81a4ee1f3efc481';
     const value = (category === WORK_CATEGORY ||
         category === TIME_OFF_CATEGORY ||
         category === ON_CALL_CATEGORY ||
         category === api.state.timeWorkedEvent.category);
     return value;
 }
