sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    const {
        data = {}
    } = event.payload;
    const {
        type,
        payload
    } = data;
    if (type === 'row-title-clicked') {
        const sysId = payload.claimsResource ? payload.claimsResource : payload.id;
        const table = 'x_snc_integrated_1_claims_resource';

        helpers.navigate.to('record', {
            sysId,
            table
        });
    }
}
