sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({ api, event, helpers, imports }) {
    const results = api.data.fetch_agents_in_my_groups.results || [];
    const agents = results.map(({ user }) => ({ id: user.value, label: user.displayValue }));

    api.setState('agentsInMyGroups', agents);
}
