sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({ api, event, helpers, imports }) {
    const { output } = api.data.fetch_session_message;
    const notifications = output && output.data ? output.data.GlideDomain_Query.session.notifications : [];
    const items = notifications.map(({ type, text }, idx) => ({
        type,
        message: text
    }));

    if (items.length > 0) {
        api.emit('SCHEDULE_PLAN_SIDEBAR#SHOW_NOTIFICATIONS', { items });
    }
}
