sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    const {savedFilterQuery = {}} = api.context.props.schedulePlanListState;
    api.setState("filterQuery", savedFilterQuery);
    const {savedScheduleFilters = {}} = api.context.props.schedulePlanListState;
    api.setState("scheduleFilters",savedScheduleFilters );
    let numAppliedFilters = Object.keys(savedScheduleFilters).length > 0 ? (Object.keys(savedScheduleFilters.sn_shift_planning_schedule_plan)).length : 0;
    api.setState("filterCount", numAppliedFilters);
    const schedulePlanListState = {...api.context.props.schedulePlanListState};
    api.setState("schedulePlanListState", schedulePlanListState);

    const searchTerm = schedulePlanListState.searchTerm &&
                        schedulePlanListState.searchTerm.length > 0 ?
                        schedulePlanListState.searchTerm : "";
    api.setState("searchTerm", searchTerm);

    const pageNumber = schedulePlanListState.pageNumber;
    if(pageNumber && pageNumber >= 0)
        api.setState("pageNumber", pageNumber);

    api.emit("SCHEDULE_PLAN_SIDEBAR_LIST_VIEW#FETCH_SCHEDULES");
}
