sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({ api, event, helpers, imports }) {
    const { newAndEditEventModal } = api.state;
    const { attendeesTypeaheadItems } = newAndEditEventModal;
    const itemSysIdSet = new Set(attendeesTypeaheadItems.map((item) => item.id));
    let newItems = [];

    if (event.elementId === 'fetch_attendees_in_my_groups') {
        const { output } = api.data.fetch_attendees_in_my_groups;
        const result = output ? output.data.GlideAggregate_Query : [];

        newItems = result
            .filter((item) => !itemSysIdSet.has(item.groupBy[0].value))
            .map((item) => ({
                id: item.groupBy[0].value,
                label: item.groupBy[0].displayValue
            }));
    } else if (event.elementId === 'fetch_all_attendees') {
        const { results = [] } = api.data.fetch_all_attendees;

        newItems = results
            .filter((item) => !itemSysIdSet.has(item.sys_id.value))
            .map((item) => ({ id: item.sys_id.value, label: item.name.value }));
    }

    api.setState('newAndEditEventModal', ({currentValue}) => {
        return {
            ...currentValue,
            attendeesTypeaheadItems: currentValue.attendeesTypeaheadItems.concat(newItems).sort((a, b) => a.label.localeCompare(b.label))
        };
    });

}
