sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({api, event, helpers, imports}) {
    let payload = event.payload;
    if(payload.items) {
        api.emit('TEAM_CALENDAR#PROPAGATE_NOTIFICATIONS_PARENT', {
          items: payload.items
        });
    }

    //Reset event popover
    api.setState('popoverTemplate', {
      "popoverState": {
          value: "destroyed"
      },
      "event": {
          "title": "",
          "isPublished": true,
          "start": "",
          "end": "",
          "description": "",
          "childEvents": [],
          "user": {"name":""},
          "participants": [{"name":""}]
      },
      "showTimeOffRequestIcon": false,
      "showShiftSwapRequestIcon": false,
      "popoverShowEditButton": false,
      "popoverShowDeleteButton": false
    });
}
