sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    const {
        data = {}
    } = event.payload;
    const {
        type,
        payload
    } = data;

    if (type === 'time-scale-popover-show-all-button-clicked') {
    api.setState('calendarControls', ({
        currentValue
    }) => {
        return {
            ...currentValue,
            currentView: "TIMELINE_DAY"
        };
    });
        api.setState("contextDate", payload.dateTime);
    } else if (type === 'row-title-clicked') {
        const sysId = payload.agent_id ? payload.agent_id : payload.id;
        const table = 'sys_user';

        helpers.navigate.to('record', { sysId, table });
    }
}
