sys_ux_client_script.script:
/**
  * @param {params} params
  * @param {api} params.api
  * @param {any} params.imports
  */
function evaluateProperty({api}) {
  let timezones = !api.state.calendarControls.timezones ? [] : api.state.calendarControls.timezones;

  let pageTimezone = api.data.initialize_team_calendar_1.output.managerTimezone;
  let pageTimezoneDisplay = api.data.initialize_team_calendar_1.output.managerTimezoneDisplayValue;
  //Only add user default timezone if it both exists and is not already one of the timezones we retrieved from a processed calendar event earlier
  if(!!pageTimezone && !timezones.some((tz) => tz.name === pageTimezone)) {
    timezones.push({
      'name': pageTimezone,
      'label': pageTimezoneDisplay
    });
  }

  timezones = timezones.sort((a, b) => a.label.localeCompare(b.label));
  return timezones;
}
