sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    try {
        //Wait for the initial Data Broker to fetch the date updates from server
        //Only allow updates to date range from calendar post initial data population
        //and check if actual date range change so as to prevent multiple API requests
        if (api.state.endDate) {
            const {
                payload: {
                    startMoment,
                    endMoment
                }
            } = event;
            const newStartDate = startMoment.format('YYYY-MM-DD');
            const newEndDate = endMoment.format('YYYY-MM-DD');

            api.setState('startDate', newStartDate);
            api.setState('endDate', newEndDate);
            let shouldRefresh = false;
            if (newStartDate !== api.state.startDate || newEndDate !== api.state.endDate)
                shouldRefresh = true;

            if (shouldRefresh) {
                helpers.timing.setTimeout(() =>  api.emit("TEAM_CALENDAR#DATE_RANGE_UPDATED"), 200);
            } 
        }

    } catch (err) {
        console.log("error with calendar date range update" + err);
    }
}
