sys_ux_client_script.script:
/**
  * @param {params} params
  * @param {api} params.api
  * @param {any} params.imports
  */
function evaluateProperty({api}) {
  try {
    let plans = api.data.fetch_schedule_plan_details.result;
    let workShifts = api.data.fetch_schedule_plans_work_shifts.output;
    if (!plans || !workShifts)
      return true;

    //Can't publish if any work shift has no agents assigned to it
    for(let i in workShifts) {
      let shift = workShifts[i];
      if(shift.agents.value.length <= 0)
        return true;
    }

    let state = plans.state.value;

    return ((state !== api.state.schedulePlanStates.DRAFT && state !== api.state.schedulePlanStates.READY_FOR_REVIEW) || workShifts.length <= 0);
  } catch (e) {
    return true;
  }
}
