sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({api, event, helpers, imports}) {
    const {model: {table, sysId}} = event.payload;

    if (table === 'sn_shift_planning_schedule_plan') {
        api.emit('SCHEDULE_PLAN_SIDEBAR#GO_TO_EDIT_VIEW', {sysId});
    } else if (table === 'sn_shift_planning_schedule_shift') {
        api.emit('SCHEDULE_PLAN_SIDEBAR#GO_TO_EDIT_WORK_SHIFT_VIEW', {schedulePlanSysId: api.context.props.sysId, workShiftSysId: sysId});
    }
}
