sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    // If switching tabs and coming back to filters tab, check if there are applied filters
    const filters = api.context.props.filters;
    if(filters && (filters.length > 0)) {
        api.setState('filters', filters);
        api.setState("loading", false);
    } else {
        api.data.fetch_filter_configuration_data_1.refresh();
    }
    api.setState('filter', api.context.props.filter);
    api.setState('filterConfig', api.context.props.filterConfig);

}
