sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    switch (event.elementId) {
        case 'confirm_and_destroy_work_shift':
            if (event.name === 'NOW_CONFIRM_DESTROY_MODAL#CONFIRM_CLICKED') {
                helpers.snHttp('/api/now/table/sn_shift_planning_schedule_shift/' + api.context.props.workShiftSysId, {
                    method: 'DELETE'
                }).then(resp => {
                    api.emit('SCHEDULE_PLAN_SIDEBAR#OPEN_SCHEDULE_PLAN', {
                        sysId: api.context.props.schedulePlanSysId
                    });
                    api.emit('SCHEDULE_PLAN_SIDEBAR#REFRESH_CALENDAR');
                }).catch(resp => {
                    if (!!resp.error) {
                        try {
                            api.emit('SCHEDULE_PLAN_SIDEBAR#SHOW_NOTIFICATIONS', {
                                items: [{
                                    status: 'critical',
                                    action: {
                                        type: 'dismiss'
                                    },
                                    content: resp.error.data.error.detail,
                                }]
                            });
                            console.log("Delete failed: " + JSON.stringify(resp.error));
                        } catch (e) {
                            console.log("Delete failed, could not provide details");
                        }
                    }
                });
            }
            break;
    }
}
