sys_ux_client_script.script:
/**
    * @param {params} params
    * @param {api} params.api
    * @param {any} params.event
    * @param {any} params.imports
    * @param {ApiHelpers} params.helpers
    */
    function handler({api, event, helpers, imports}) {

        const {preferences} = api.data.look_up_user_preferences.result;

        if(preferences && preferences.length > 0){
            const pref = Number(preferences[0].value);
            if(pref && pref > 0) {
                api.setState('pageSize', pref);
                let pageSizes = api.state.pageSizes;
                if(!pageSizes.includes(pref)){
                    pageSizes.push(pref);
                    pageSizes.sort((a, b) => a - b);
                    api.setState('pageSizes', pageSizes);
                }
            }
        }

    }
