sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    console.log('running Update Timeline Sections')


    const {
        timelineSections
    } = api.data.get_claims_resource_calendar_data_1.output;

    var newSections = timelineSections.map(section => {
        var coverageMap = new Map();
        coverageMap.set('coverage-TIMELINE_WEEK', section.runtimeData);
        section.runtimeData = coverageMap;
        return section;
    });

    console.log('newSections', newSections)
    api.setState('timelineSections', newSections);
}
