sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
async function handler({api, event, helpers, imports}) {
    let labelEndBreak = await helpers.translate("End Break");
    if("button_remove_break" === event.elementId) {
        let indexToRemove = event.context.item.index;
        let breaks = [...api.state.editShiftControls.breakInfo.breaks];
        breaks.splice(indexToRemove,1);
        api.setState("editShiftControls", ({currentValue})=> {
            return {
                ...currentValue,
                breakInfo: {
                    ...currentValue.breakInfo,
                    breaks : breaks
                },
                formDirty: true
            };
        });
    } else if ("button_add_break" === event.elementId) {
        api.setState("editShiftControls", ({currentValue})=> {
            return {
                ...currentValue,
                breakInfo: {
                    ...currentValue.breakInfo,
                    breaks : [...currentValue.breakInfo.breaks, {
                        breakStartTime: "",
                        breakEndTime: "",
                        invalid: false,
                        breakEventEndLabel: labelEndBreak
                    }]
                },
                formDirty: true
            };
        });
    }
}
